mapper.select:
	SELECT * 
 	 FROM challenge_like
    WHERE challenge_board_id = #{boardId}
	  AND writer = #{writer}

mapper.delete:
	DELETE FROM challenge_like
     WHERE challenge_board_id = #{boardId}
	   AND writer = #{writer}

