mapper.update:
UPDATE proof_board
SET title = #{title},
content = #{content}
WHERE proof_board_id =
#{proofBoardId}

mapper.delete:
DELETE FROM
proof_board
WHERE challenge_board_id = #{challengeBoardId}
AND writer =
#{writer}

mapper.update:
UPDATE proof_board
SET view_count = view_count + 1
WHERE proof_board_id = #{proofBoardId}
