mapper.delete:
DELETE 
FROM user_follow
WHERE follower_nick_name = #{followerNickName}
  AND following_nick_name = #{followingNickName}

mapper.select:
SELECT follower_count AS followerCount, following_count AS followingCount
FROM user_board
WHERE user_board_id = #{userBoardId}

SELECT follower_nick_name
FROM user_follow
WHERE following_nick_name = #{userNickName}

SELECT following_nick_name
FROM user_follow
WHERE follower_nick_name = #{userNickName}

		SELECT EXISTS (
 			 SELECT 1 FROM user_board WHERE nick_name = #{nickName}
					) AS is_exists;
